import React, {Component} from 'react';
import './todo.css';
import ToDoItem from './Components/todoItem';
import Logo from './logo.svg';

class ToDo extends Component {
    constructor(props){
        super(props);
    this.state = {
        //Entrada de dados AKA Reuse ou mock
        list : [
            {'todo': 'Limpar a casa'},
            {'todo':'Comprar Leite'}
        ],
        todo:''
    };
    };

        //Construtor AKA Create
    createNewToDoItem = () => {
        this.setState(({list,todo}) =>({
        list:[
            ...list,
            {
                todo
            }
        ],
        todo:''
    }));    

};

handleKeyPress = e => {
    this.setState({
        todo: e.target.value
    });
};


}

export default ToDo;
