import React, {Component} from 'react';
import './todoItem.css';

class ToDoItem extends Component {

    handleCheck = e => {
        if (this.myInput.value !== '') {

                this.createNewToDoItem();

        }
    };

    render() {

        return (
            <div ref={item => {this.myItem = item;}} className="ToDoItem">
                <p className="ToDoItem-Text">{this.props.item}</p>
                <button className="ToDoItem-Check"
                     onClick={this.props.deleteItem}>&#x2713;
                </button>
                <button className="ToDoItem-Delete"
                     onClick={this.props.deleteItem}>-
                </button>
            </div>
        );
    }
}

export default ToDoItem;
