import React, {Component} from 'react';
import './todo.css';
import ToDoItem from './Component/todoItem';
import Logo from './logo.svg';

class ToDo extends Component {
    constructor(props){
        super(props);
    this.state = {
        //Entrada de dados AKA Reuse ou mock
        list : [
            {'todo': 'Limpar a casa'},
            {'todo':'Comprar Leite'}
        ],
        todo:''
    };
    };

        //Construtor AKA Create
    createNewToDoItem = () => {
        this.setState(({list,todo}) =>({
        list:[
            ...list,
            {
                todo
            }
        ],
        todo:''
    }));    

};
        //BotÃ£o do construtor
handleKeyPress = e => {
    if (e.target.value !== ''){
        if(e.key === 'Enter'){
            this.createNewToDoItem()
        }
    }
};

        //Bind de entrada
handleInput = e => {
    this.setState({
        todo: e.target.value
    });
};

        // Delete item
deleteItem = indexToDelete => {
    this.setState(({list}) => ({
        list: list.filter((ToDo,list)!== indexToDelete)
    }))
}
render() {
    return (
        <div className='ToDo'>
            <img className='Logo' src={Logo} alt='React-logo' />
            <h1 className='ToDo-Header'>React To Do</h1>
            <div className='ToDo-Container'>
                <div className='ToDo-Content'>
                    {this.state.list.map((item,key)=>{
                        return<ToDoItem
                            key={key}
                            item={item.todo}
                            deleteItem = {this.deleteItem.bind(this.key)}
                            />
                    }
                    )}
                    </div>
                    <div>
                        <input type='text' value={this.state.todo} onChange={this.handleInput} onKeyPres={this.handleKeyPress}/>
                        <button className='ToDo-Add' onClick={this.createNewToDoItem}>+</button>
                        </div>
                        </div>
                        </div>


    );
}
}

export default ToDo;
