import React, {Component} from 'react';
import './todoItem.css';

class ToDoItem extends Component {






    render() {        
        return (
            <div className="ToDoItem">
                <p ref={input => {this.myInput = input;}} 
                    type="text" value={this.state.todo}
                    className="ToDoItem-Text">
                        {this.props.item}
                </p>
                <button ref={check => {this.myCheck = check;}} 
                     type="text" value={this.state.todo}
                     className="ToDoItem-Check"
                     onClick={this.props.deleteItem}
                        >
                            &#x2713;
                </button>
                <button className="ToDoItem-Delete"
                     onClick={this.props.deleteItem}>-
                </button>
            </div>
        );
    }
}

export default ToDoItem;
